mapper.insert:
		BEGIN

        	SELECT ARTICLE_SEQ.nextval FROM dual

		INSERT INTO ARTICLE a VALUES (
			#{id}
			,	#{company_id}
			,	#{title}
			,	#{career}
			,	#{salary}
			,	(SELECT ad.ID FROM ADDRESS ad WHERE ad.DISTRICT = #{address})
			,	#{detail_addr}
			,	#{need_amt}
			,	#{job_explain}
			,	#{deadline}
			,	#{qualify}
		)
		;

		INSERT INTO JOB j (
			j.ID ,
			j.ARTICLE_ID ,
			j.NAME 
		)
		SELECT JOB_SEQ.nextval, B.* from (

				SELECT
					#{id},
					#{name}
				FROM dual

		)B;

		INSERT INTO SKILL_STACK ss (
			ss.ID ,
			ss.ARTICLE_ID ,
			ss.NAME 
		)
		SELECT JOB_SEQ.nextval, C.* from (

				SELECT
					#{id},
					#{name}
				FROM dual

		)C;
		END;

mapper.select:
SELECT j.NAME 
FROM JOB j 
WHERE j.ARTICLE_ID = #{id}

SELECT ss.NAME 
FROM SKILL_STACK ss 
WHERE ss.ARTICLE_ID = #{id}
