	,	#{detail_addr}
	,	#{need_amt}
	,	#{job_explain}
	,	#{deadline}
	,	#{qualify}
)

INSERT INTO JOB j VALUES (
	JOB_SEQ.nextval
	,	(SELECT max(a.ID) FROM ARTICLE a)
	,	#{name}
)

INSERT INTO SKILL_STACK ss VALUES (
	SKILL_STACK_SEQ.nextval
