mapper.insert:
 		INSERT INTO "MEMBER" m VALUES (
			MEMBER_SEQ.nextval
			,	#{userid}
			,	#{password}
			,	#{name}
			,	(SELECT g.ID FROM GENDER g WHERE g.TITLE = #{gender})
			,	#{birthday}
			,	#{phone}
			,	#{email}
			,	(SELECT a.ID FROM ADDRESS a WHERE a.DISTRICT = #{address})
			,	#{detail_addr}
		)
